import React from 'react';
import root from 'react-shadow';
import Tooltip from '../src';
import lipsum from 'bob-ross-lipsum';

export default function Shadow(args) {
  return (
    <root.div className='shadow-host'>
      <Tooltip
        root={ document.documentElement }
        message={ lipsum(3) }
        data-eid='testing.shadow_tooltip.test_my_heart_out.button.click'
        { ...args }>
      </Tooltip>
    </root.div>
  );
}

Shadow.args = {
  id: 'shadow-tooltip',
  title: 'My shadow DOM tooltip',
  text: 'Click here for tooltip outside Shadow DOM'
};
