import Intents, { enums } from '@ux/intents';

export default (function () {
  const intents = new Intents();

  intents.create('@ux/tooltip', {
    styleProperties: [
      enums.properties.FONT_FAMILY,
      enums.properties.FONT_SIZE,
      enums.properties.FONT_WEIGHT,
      enums.properties.LINE_HEIGHT,
      enums.properties.FOREGROUND_COLOR,
      enums.properties.BACKGROUND_COLOR,
      enums.properties.BORDER_COLOR,
      enums.properties.BORDER_RADIUS
    ],
    features: {
      [enums.features.TITLE]: {
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT
        ]
      }
    }
  });

  intents.uxTooltip.fontFamily = intents.ux.text.fontFamily;
  intents.uxTooltip.fontSize = intents.ux.text.fontSize;
  intents.uxTooltip.fontWeight = intents.ux.text.fontWeight;
  intents.uxTooltip.lineHeight = intents.ux.text.lineHeight;
  intents.uxTooltip.foregroundColor = intents.ux.boxHighContrast.foregroundColor;
  intents.uxTooltip.backgroundColor = intents.ux.boxHighContrast.backgroundColor;
  intents.uxTooltip.borderColor = intents.ux.boxHighContrast.borderColor;
  intents.uxTooltip.borderRadius = intents.ux.box.borderRadius;

  intents.uxTooltip.title.fontFamily = intents.ux.textLabel.fontFamily;
  intents.uxTooltip.title.fontSize = intents.ux.textLabel.fontSize;
  intents.uxTooltip.title.fontWeight = intents.ux.textLabel.fontWeight;
  intents.uxTooltip.title.lineHeight = intents.ux.textLabel.lineHeight;

  return intents;
}());
