const { plugins, presets, sourceType } = require('@ux/babel-config');

module.exports = api => {
  const nextPlugins = [...plugins];
  // Remove `propTypes` for production builds.
  if (api.env('production')) {
    nextPlugins.push('babel-plugin-transform-react-remove-prop-types');
  }

  return {
    presets,
    plugins: nextPlugins,
    sourceType
  };
};
